package chanwook.cooker;import javax.servlet.http.Cookie;import java.util.HashMap;import java.util.Map;/** * Created by chanwook on 2014. 3. 1.. */public class Cooker {    private static final String DEFAULT_CHARSET_NAME = "UTF-8";    private Map<String, Cookie> cookieMap = new HashMap<String, Cookie>();    private String charsetName = DEFAULT_CHARSET_NAME;    public Cooker() {    }    public Cooker(String charsetName) {        this.charsetName = charsetName;    }    public CookiePrototype cooking(String key, String value) {        Cookie cc = new Cookie(key, value);        cookieMap.put(key, cc);        return new CookiePrototype(key, this);    }    public Cookie cooking(Cookie cookie) {        cookieMap.put(cookie.getName(), cookie);        return null;    }    public Cookie getCookie(String key) {        return cookieMap.get(key);    }    public CookiePrototype getPrototype(String key) {        return new CookiePrototype(key, this);    }    public String getCharsetName() {        return charsetName;    }    public void setCharsetName(String charsetName) {        this.charsetName = charsetName;    }}